import './App.css';
import React, {Component} from "react";
import {BrowserRouter as Router, Redirect, Route} from 'react-router-dom'
import Manufacturers from '../Manufacturers/manufacturers';
import Categories from '../Categories/categories';
import Products from '../Products/ProductList/products';
import Header from '../Header/header';
import ProductAdd from '../Products/ProductAdd/productAdd';
import EShopService from "../../repository/eshopRepository";
import ProductEdit from "../Products/ProductEdit/productEdit";

class App extends Component {

  constructor(props) {
    super(props);
    this.state = {
      manufacturers: [{name: "smt", address: "smt"}], //authors //surname
      products: [], //books
      categories: [{name: "smt", description: "smt"}],
      selectedProduct: {} //selectedBooks
    }
  }

  render() {
    return (
        <Router>
          <Header/>
          <main>
            <div className="container">
              <Route path={"/manufacturers"} exact render={() => // /authors
                  <Manufacturers manufacturers={this.state.manufacturers}/>}/>
              <Route path={"/categories"} exact render={() =>
                  <Categories categories={this.state.categories}/>}/>
              <Route path={"/products/add"} exact render={() => // /books/add
                  <ProductAdd categories={this.state.categories}
                              manufacturers={this.state.manufacturers}
                              onAddProduct={this.addProduct}/>}/>
              <Route path={"/products/edit/:id"} exact render={() => // /books/edit/:id
                  <ProductEdit categories={this.state.categories}
                               manufacturers={this.state.manufacturers}
                               onEditProduct={this.editProduct}
                               product={this.state.selectedProduct}/>}/>
              <Route path={"/products"} exact render={() => // /books
                  <Products products={this.state.products}
                            onDelete={this.deleteProduct}
                            onEdit={this.getProduct}/>}/>
                {/* /books*/}
              <Redirect to={"/products"}/>
            </div>
          </main>
        </Router>
    );
  }

  componentDidMount() {
    this.loadManufacturers(); // this.loadAuthors();
    this.loadCategories();
    this.loadProducts(); // this.loadBooks();
  }

  loadManufacturers = () => { // loadAuthors
    EShopService.fetchManufacturers() //fetchAuthors
        .then((data) => {
          this.setState({
            manufacturers: data.data //authors
          })
        });
  }

  loadProducts = () => { //loadBooks
    EShopService.fetchProducts() //fetchBooks
        .then((data) => {
          this.setState({
            products: data.data //books
          })
        });
  }

  loadCategories = () => {
    EShopService.fetchCategories()
        .then((data) => {
          this.setState({
            categories: data.data
          })
        });
  }

  deleteProduct = (id) => { //deleteBook
    EShopService.deleteProduct(id) //EShopService.deleteBook(id)
        .then(() => {
          this.loadProducts(); // this.loadBooks();
        });
  }

  addProduct = (name, price, quantity, category, manufacturer) => { //addBook
    EShopService.addProduct(name, price, quantity, category, manufacturer) //addBook
        .then(() => {
          this.loadProducts(); //loadBooks();
        });
  }

  getProduct = (id) => { //getBook
    EShopService.getProduct(id) //getBook(id)
        .then((data) => {
          this.setState({
            selectedProduct: data.data //selectedBook
          })
        })
  }

  editProduct = (id, name, price, quantity, category, manufacturer) => { //editBook
    EShopService.editProduct(id, name, price, quantity, category, manufacturer) //editBook
        .then(() => {
          this.loadProducts(); //this.loadBook();
        });
  }
}

export default App;
