import axios from '../custom-axios/axios';

const EShopService = { //LibraryService
    fetchManufacturers: () => { //fetchAuthors
        return axios.get("/manufacturers"); // /authors
    },
    fetchCategories: () => {
        return axios.get("/categories");
    },
    fetchProducts: () => { //fetchBooks
        return axios.get("/products"); // /books
    },
    deleteProduct: (id) => { //deleteBook
        return axios.delete(`/products/delete/${id}`); // /books/delete/${id}
    },
    addProduct: (name, price, quantity, category, manufacturer) => { //addBook, izbrishi price, authors
        return axios.post("/products/add", { // /book/add
            "name" : name,
            "price" : price, //izbrishi
            "quantity" : quantity,
            "category" : category,
            "manufacturer" : manufacturer //author
        });
    },
    editProduct: (id, name, price, quantity, category, manufacturer) => { //editBook, izbrishi price, authors
        return axios.put(`/products/edit/${id}`, { //books/edit
            "name" : name,
            "price" : price, //izbrishi
            "quantity" : quantity,
            "category" : category,
            "manufacturer" : manufacturer //author
        });
    },
    getProduct: (id) => { //getBook
        return axios.get(`/products/${id}`); // /book/${id}
    }
}

export default EShopService; //LibraryService
